mapper.select:
select agent.ID, IFNULL(agent.USER_ID,'') as USER_ID, IFNULL(agent.URL,'') as URL, IFNULL(agent.DESCRIPTION,'') as DESCRIPTION, IFNULL(date_format(agent.CREATE_TIME, '%Y-%m-%d'),'') as CREATE_TIME, IFNULL(date_format(agent.UPDATE_TIME, '%Y-%m-%d'),'') as UPDATE_TIME, IFNULL(agent.STATUS,'') as STATUS, IFNULL(user.USER_NAME,'') as USER_NAME, IFNULL(user.PHONE,'') as PHONE, IFNULL(user.EMAIL_ADDRESS,'') as EMAIL_ADDRESS from D_AGENT_APPLY agent inner join D_USER user on agent.USER_ID = user.ID where 1=1
and agent.STATUS = #{status}
order by ID desc

mapper.update:
update D_AGENT_APPLY
set STATUS = #{status},DESCRIPTION=#{description},UPDATE_TIME = #{updateTime}
where ID = #{id}
